mapper.select:
SELECT * FROM comment c
    WHERE blog_id != 0 AND parent_comment_id = ${parentId}
    ORDER BY c.gmt_create DESC

SELECT * FROM comment c
    WHERE blog_id = 0 AND parent_comment_id = ${parentId}
    ORDER BY c.gmt_create DESC

SELECT * FROM comment c
    WHERE parent_comment_id = ${id}
    ORDER BY c.gmt_create DESC

SELECT * FROM comment c
    WHERE parent_comment_id = ${childId}
    ORDER BY c.gmt_create DESC
